// sum.test.js
import { expect, test } from "vitest";
import { getLongestPalindrome } from "./index";

test("basic", () => {
  expect(getLongestPalindrome("ababc")).toBe(3);
  expect(getLongestPalindrome("abbba")).toBe(5);
  expect(getLongestPalindrome("b")).toBe(1);
  expect(getLongestPalindrome("bbb")).toBe(3);
  expect(
    getLongestPalindrome(
      "",
    ),
  ).toBe(6);
});
